import React, { useMemo } from "react";

import DataTable from "react-data-table-component";
import FilterComponent from "./FilterComponent";

const allTable = props => {
  const columns = [
    {
      name: "Transaction Date",
      selector: "transactionDate",
      sortable: true,
      //grow: 2
    },
    {
      name: "Description",
      selector: "description",
      sortable: true,
      hide: "sm"
    },
    {
      name: "Category",
      selector: "category",
      sortable: true
    },
    {
      name: "Debit",
      selector: "debit",
      sortable: true,
      hide: "md"
    },
    {
      name: "Credit",
      selector: "credit",
      sortable: true,
      hide: "md"
    },
    {
      name: "ID",
      selector: "id",
      sortable: true,
      hide: "md"
    },
];
}
export default allTable;
